from spellchecker import SpellChecker
import re

# Normalize words by collapsing repeated letters
def normalize_word(word):
    return re.sub(r'(.)\1+', r'\1', word.lower())

# Check if a word is in the slang set (after normalization)
def is_slang(word, slang_set):
    return normalize_word(word) in slang_set

